mapper.select:
select
from t_attribute_info where id in (select attribute_id from t_node_attribute where node_id = #{nodeId,jdbcType=INTEGER})
and type = #{type,jdbcType=INTEGER}

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into t_attribute_info (type, name, remark, 
  status, create_time, update_time
  )
values (#{type,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}, 
  #{status,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )
