select u.email,u.nick_name as name,c.create_time as createTime,c.consumption,c.commission,c.ratio from t_commission_detail as c left join t_user_info as u on u.user_id = c.low_user_id where c.user_id = #{userId,jdbcType=VARCHAR}
and (u.phone = #{account,jdbcType=VARCHAR} or u.email = #{account,jdbcType=VARCHAR})
and c.create_time > #{startTime,jdbcType=VARCHAR}
and #{endTime,jdbcType=VARCHAR} > c.create_time
order by c.id desc

SELECT tup.user_id as userId, IFNULL(SUM(tcd.consumption), 0.0) as consumptionTotal, IFNULL(SUM(tcd.commission), 0.0) as commissionTotal FROM t_user_pro tup LEFT JOIN t_commission_detail tcd ON tup.user_id = tcd.low_user_id -- 被推广人ID → 消费用户ID WHERE tup.user_id IN
#{item}
GROUP BY tup.user_id

SELECT u.phone, u.email, u.nick_name as name, c.consumption, c.commission, c.ratio, c.create_time as createTime FROM t_user_pro as up LEFT JOIN t_commission_detail as c ON up.user_id = c.low_user_id LEFT JOIN t_user_info as u ON up.user_id = u.user_id WHERE up.pro_user_id = #{userId,jdbcType=VARCHAR}
AND (u.phone = #{account,jdbcType=VARCHAR} OR u.email = #{account,jdbcType=VARCHAR})
AND c.create_time > #{startTime,jdbcType=VARCHAR}
and #{endTime,jdbcType=VARCHAR} > u.create_time
ORDER BY c.create_time DESC
