mapper.select:
select nd.id, nd.node_id, nd.model_id, nd.disk_type, nd.model_param, nd.min_num, nd.max_num, nd.item_num, nd.give_num, nd.extend_bl, nd.data_bl, nd.remark, nd.status, nd.create_time, nd.update_time, case when nm.id is null then null else CONCAT(nm.cpu_val,'C', nm.ram_val,'G') end as modelConfig from t_node_disk as nd left join t_node_model as nm on nm.id = nd.model_id where 1 = 1
and nd.node_id = #{nodeId,jdbcType=INTEGER}
order by nd.id desc

select

from t_node_disk
where
node_id = #{nodeId,jdbcType=INTEGER}

  and model_id = #{modelId,jdbcType=INTEGER}


  and model_id is null
