mapper.select:
select

from t_offline_finance
where
1 = 1

  and way = #{way,jdbcType=INTEGER}


  and direction = #{direction,jdbcType=INTEGER}


  and tag = #{tag,jdbcType=INTEGER}


  and association like '%${association}%'


  and remark like '%${remark}%'


  and create_time >= #{startTime,jdbcType=VARCHAR}


  and #{endTime,jdbcType=VARCHAR} > create_time

order by id desc

select IFNULL(sum(amount_num),0.0) as numerical from t_offline_finance where direction = 0
and occur_time >= #{startDay,jdbcType=VARCHAR}
and #{endDay,jdbcType=VARCHAR} > occur_time

select IFNULL(sum(CASE WHEN tag = 0 THEN amount_num END),0.0) as offlineCommission, IFNULL(sum(CASE WHEN tag = 1 THEN amount_num END),0.0) as offlineWithdrawal, IFNULL(sum(CASE WHEN tag = 2 THEN amount_num END),0.0) as procure from t_offline_finance where direction = 1
and occur_time >= #{startDay,jdbcType=VARCHAR}
and #{endDay,jdbcType=VARCHAR} > occur_time
