mapper.select:
select p.id, p.superior_id, p.superior_level, p.province_name, p.sorting, p.remark, p.status, p.create_time, p.update_time, case when p.superior_level = 2 then cou.country_name else con.continent_name end as superiorName from t_region_province as p left join t_region_country as cou on cou.id = p.superior_id left join t_region_continent as con on con.id = p.superior_id where 1 = 1
and p.superior_id = #{superiorId,jdbcType=INTEGER}
and p.superior_level = #{superiorLevel,jdbcType=INTEGER}
and p.province_name like '%${provinceName}%'
and p.status = #{status,jdbcType=INTEGER}
order by p.id desc

select

from t_region_province
where superior_id = #{id,jdbcType=INTEGER} and superior_level = #{type,jdbcType=INTEGER}
