mapper.select:
select
,
from t_sys_log where user_id = #{userId,jdbcType=VARCHAR}
and status = #{status,jdbcType=INTEGER}
order by id desc

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into t_sys_log (type, user_id, status, 
  remark, create_time, content
  )
values (#{type,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, 
  #{remark,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{content,jdbcType=LONGVARCHAR}
  )
